<?phpclass ajax extends application {				function __construct( $app, &$obj ){  		parent::__construct( $app, &$obj );	}	public function ajax(){		if( isset( $_POST[ "f" ] ) && $_POST[ "f" ] != "" ){			if( method_exists( $this, $_POST[ "f" ] ) ){  				$this->$_POST[ "f" ]();			}		}		$this->ajaxMimox();	}	public function ajaxMimox(){		$jsonArr = array();		$var = array(			"{$_POST[ "thisString" ]}%"		); 		$resArr = $this->model->query( "select * from telepulesek where telepules like ?;", "select", $var ); 		foreach( $resArr as $key => $item ){			$jsonArr[] = $item[ "telepules" ];		}		$this->content = json_encode( $jsonArr );	}}