<?phpclass mimox extends application {				function __construct( $app, &$obj ){  		parent::__construct( $app, &$obj );	}	public function mimox(){		$formElements[ "mimox" ] = array(			"isDom" => "false",			"action" => "",			"method" => "post",			"charset" => "UTF-8"		);		$formElements[ "mimox" ][ "elements" ][] = array(				"text" => array(				"label" => "mimoxTest",				"id" => "mimoxId",				"class" => "mimoxClass",				"name" => "mimox",				"rel" => "ddd=mmm",				"value" => ""			)		);		$thisForm = new form( $this, $formElements ); 		$this->content = $thisForm->content;	}}